sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
var gr = new GlideDateTime(); // Today's date
var gr1 = new GlideDateTime(current.u_when_needed); // Selected date on form
	/* if result is -1 i.e. wn is less than td i.e. selected date is less than today's date */
	if(gr1.before(gr)) { 
		gs.info("Date cant be entered as date selected is date before the today's date");
		current.setAbortAction(true);
	}

var td = gr.getLocalDate();
var wn = gr1.getLocalDate();
var rt = td.compareTo(wn); // comparing today's date with selected date
	/* if result is 0 then abort action */
	if(rt == 0) { 
		gs.info("Date selected is today's date");
		current.setAbortAction(true);
	}

	/* if result is 1 i.e. wn is more than td i.e. selected date is more than today's date */
	if(rt == 1) { 
		gs.info("Date is successfully entered as date selected is after today's date");
			}
})(current, previous);
